package Java;

import java.util.Scanner;

public class a10869 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scanner = new Scanner(System.in);
		
		int a,b = 0;
		 
		a = scanner.nextInt();
		b = scanner.nextInt();
		 
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
	}
}
